mapper.insert:
INSERT INTO  express.expressinfo(id,express_no, from_date, arrive_date, receive_date, company
   , consignee, contact, verification_code, address_source, address_dest, express_status)
VALUES (#{id},#{expressNo},#{fromDate}, #{arriveDate},#{receiveDate},#{company},#{consignee},#{contact}
,#{verificationCode},#{addressSource},#{addressDest},#{status});

mapper.select:
SELECT id,express_no, from_date, arrive_date, receive_date, company , consignee, contact, verification_code, address_source, address_dest, express_status FROM express.expressinfo where 1=1
and contact=#{contact}
and express_no=#{expressNo}
