mapper.select:
SELECT
id,express_no, from_date, arrive_date, receive_date, company
,
consignee, contact, verification_code, address_source, address_dest,
express_status, email_address
FROM express.expressinfo
where
1=1

	and DATE_FORMAT(from_date,'%Y-%m-%d')=DATE_FORMAT(#{fromDate},'%Y-%m-%d')


	and DATE_FORMAT(arrive_date,'%Y-%m-%d')=DATE_FORMAT(#{arriveDate},'%Y-%m-%d')



	and express_no=#{expressNo}


	and company=#{company}


	and consignee=#{consignee}


	and contact=#{contact}


	and express_status=#{status}

order by arrive_date desc

SELECT
id,express_no, from_date, arrive_date, receive_date, company
,
consignee, contact, verification_code, address_source, address_dest,
express_status, email_address
FROM express.expressinfo


		id = #{id}


		and express_no = #{expressNo}


		and express_status = #{status}

SELECT id,express_no, from_date, arrive_date, receive_date, company , consignee, contact, verification_code, address_source, address_dest, express_status, email_address FROM express.expressinfo where 1=1
and contact=#{contact}
and express_no=#{expressNo}
and express_status=#{status}
order by arrive_date

SELECT id,express_no, from_date, arrive_date, receive_date, company , consignee, contact, verification_code, address_source, address_dest, express_status, email_address FROM express.expressinfo where express_status in ('E','O')
and DATE_FORMAT(from_date,'%Y-%m-%d')=DATE_FORMAT(#{fromDate},'%Y-%m-%d')
and DATE_FORMAT(arrive_date,'%Y-%m-%d')=DATE_FORMAT(#{arriveDate},'%Y-%m-%d')
and express_no=#{expressNo}
and company=#{company}
and consignee=#{consignee}
and contact=#{contact}
order by arrive_date desc
